local _,Analyzer=...if not Analyzer then return end
local module={}
local utils=Analyzer.utils
module.name="Death Knight - Frost"
module.class="DEATHKNIGHT"
module.specKey="frost"
module.specIndex=2
module.specId=251
local SPELL_OBLITERATE=49020
local SPELL_FROST_STRIKE=49143
local SPELL_HOWLING_BLAST=49184
local SPELL_SOUL_REAPER=130735
local SPELL_PILLAR_OF_FROST=51271
local SPELL_OUTBREAK=77575
local SPELL_PLAGUE_STRIKE=45462
local SPELL_BLOOD_PLAGUE=55078
local SPELL_FROST_FEVER=55095
local SPELL_KILLING_MACHINE=51124
local SPELL_RIME=59052
local SPELL_EMPOWER_RUNE_WEAPON=47568
local COOLDOWN_PILLAR_OF_FROST=60
local COOLDOWN_EMPOWER_RUNE=300
local SOUND_KEY_KILLING_MACHINE="killingMachine"
local SOUND_KEY_RIME="rime"
local SOUND_KEY_PILLAR_OF_FROST="pillarOfFrost"
local TRACKED_BUFFS={[SPELL_PILLAR_OF_FROST]=true,[SPELL_KILLING_MACHINE]=true,[SPELL_RIME]=true}
local TRACKED_DEBUFFS={[SPELL_BLOOD_PLAGUE]=true,[SPELL_FROST_FEVER]=true}
local TRACKED_CASTS={[SPELL_OBLITERATE]=true,[SPELL_FROST_STRIKE]=true,[SPELL_HOWLING_BLAST]=true,[SPELL_SOUL_REAPER]=true,[SPELL_PILLAR_OF_FROST]=true,[SPELL_OUTBREAK]=true,[SPELL_PLAGUE_STRIKE]=true,[SPELL_EMPOWER_RUNE_WEAPON]=true}
local function EH(c,s)c[s]=c[s]or{}return c[s]end
function module.SupportsSpec(a)if a.player.class~=module.class then return false end if a.player.specId==module.specId then return true end local sk=utils.NormalizeSpecKey(a.player.specName)if sk==module.specKey then return true end if a.player.specIndex==module.specIndex then return true end return false end
function module.OnPlayerInit(a)if a.player.class~=module.class then return end if a.player.specName~="Unknown"then return end if IsSpellKnown and IsSpellKnown(SPELL_OBLITERATE)and IsSpellKnown(SPELL_HOWLING_BLAST)then a.player.specName="Frost"a.player.specId=a.player.specId or module.specId a.player.specIndex=a.player.specIndex or module.specIndex end end
function module.GetDefaultSettings()return{sounds={[SOUND_KEY_KILLING_MACHINE]={enabled=true,sound="Sound\\Interface\\RaidWarning.ogg"},[SOUND_KEY_RIME]={enabled=true,sound="Sound\\Interface\\MapPing.ogg"},[SOUND_KEY_PILLAR_OF_FROST]={enabled=true,sound="Sound\\Interface\\MapPing.ogg"}}}end
function module.GetSoundOptions()return{{key=SOUND_KEY_KILLING_MACHINE,label="Killing Machine Proc"},{key=SOUND_KEY_RIME,label="Rime Proc"},{key=SOUND_KEY_PILLAR_OF_FROST,label="Pillar of Frost Ready"}}end
function module.IsTrackedBuffSpell(s)return TRACKED_BUFFS[s]==true end
function module.IsTrackedDebuffSpell(s)return TRACKED_DEBUFFS[s]==true end
function module.IsTrackedCast(s)return TRACKED_CASTS[s]==true end
function module.ShouldRecordCast(e)if not e or not e.spellId then return false end return TRACKED_CASTS[e.spellId]==true end
function module.GetProcInfo(s)if s==SPELL_KILLING_MACHINE then return{soundKey=SOUND_KEY_KILLING_MACHINE,priority=3}elseif s==SPELL_RIME then return{soundKey=SOUND_KEY_RIME,priority=2}end return nil end
function module.InitFight(_,f)f.procs={killingMachineProcs=0,killingMachineConsumed=0,rimeProcs=0,rimeConsumed=0}f.counts={obliterateCasts=0,frostStrikeCasts=0,soulReaperCasts=0}f.cooldowns={pillarLast=0,empowerLast=0}f.castLog={}f.buffHistory={}f.debuffHistory={}f.rotation={totalCasts=0,optimalCasts=0,suboptimalCasts=0,mistakes={},penaltySum=0}end
function module.TrackSpellCast(a,s,t)local f=a.fight if not f or not TRACKED_CASTS[s]then return end local n=utils.NormalizeTimestamp(t)f.spells[s]=(f.spells[s]or 0)+1 table.insert(f.castLog,{spellId=s,timestamp=n})if s==SPELL_OBLITERATE then f.counts.obliterateCasts=f.counts.obliterateCasts+1 local b=f.buffs[SPELL_KILLING_MACHINE]if b then f.procs.killingMachineConsumed=f.procs.killingMachineConsumed+1 f.buffs[SPELL_KILLING_MACHINE]=nil end elseif s==SPELL_FROST_STRIKE then f.counts.frostStrikeCasts=f.counts.frostStrikeCasts+1 elseif s==SPELL_HOWLING_BLAST then local b=f.buffs[SPELL_RIME]if b then f.procs.rimeConsumed=f.procs.rimeConsumed+1 f.buffs[SPELL_RIME]=nil end elseif s==SPELL_SOUL_REAPER then f.counts.soulReaperCasts=f.counts.soulReaperCasts+1 a:AddEventLog(n,"Soul Reaper",s)elseif s==SPELL_PILLAR_OF_FROST then f.cooldowns.pillarLast=n a:AddTimelineEvent(s,n,"cooldown")a:AddEventLog(n,"Pillar of Frost",s)elseif s==SPELL_EMPOWER_RUNE_WEAPON then f.cooldowns.empowerLast=n a:AddTimelineEvent(s,n,"cooldown")a:AddEventLog(n,"Empower Rune Weapon",s)end local cS={[SPELL_OBLITERATE]=true,[SPELL_FROST_STRIKE]=true,[SPELL_HOWLING_BLAST]=true,[SPELL_SOUL_REAPER]=true}if cS[s]and f.rotation then local ev=module.EvaluateCast(a,f,s,n)if ev then module.RecordCastEvaluation(f,ev,n)end end end
function module.TrackAura(a,se,s,am,t)local f=a.fight if not f then return end local n=utils.NormalizeTimestamp(t)if se=="SPELL_AURA_APPLIED"or se=="SPELL_AURA_APPLIED_DOSE"or se=="SPELL_AURA_REFRESH"then local st=am or 1 local b=f.buffs[s]local isR=(se=="SPELL_AURA_REFRESH")if b and isR and b.since then f.buffUptime[s]=(f.buffUptime[s]or 0)+(n-b.since)end if not b then b={stacks=st,since=n}f.buffs[s]=b else b.stacks=st b.since=b.since or n end if isR then b.since=n end local h=EH(f.buffHistory,s)if isR and b.historyEntry and not b.historyEntry.removed then b.historyEntry.removed=n end if not b.historyEntry or isR then b.historyEntry={applied=n}table.insert(h,b.historyEntry)end if s==SPELL_KILLING_MACHINE then if not isR then f.procs.killingMachineProcs=f.procs.killingMachineProcs+1 a:AddTimelineEvent(s,n,"proc")end a:PlayAlertSound(SOUND_KEY_KILLING_MACHINE,n)elseif s==SPELL_RIME then if not isR then f.procs.rimeProcs=f.procs.rimeProcs+1 end a:PlayAlertSound(SOUND_KEY_RIME,n)end elseif se=="SPELL_AURA_REMOVED"then local b=f.buffs[s]if b then if b.since then f.buffUptime[s]=(f.buffUptime[s]or 0)+(n-b.since)end if b.historyEntry and not b.historyEntry.removed then b.historyEntry.removed=n end end f.buffs[s]=nil end end
function module.TrackDebuff(a,se,s,dG,dN,t)local f=a.fight if not f or not TRACKED_DEBUFFS[s]then return end local n=utils.NormalizeTimestamp(t)if not f.primaryTargetGUID then f.primaryTargetGUID=dG end if dG and f.primaryTargetGUID and dG~=f.primaryTargetGUID then return end if dG then f.targets[dG]=true end local d=f.debuffs[s]local h=EH(f.debuffHistory,s)if se=="SPELL_AURA_APPLIED"then f.debuffs[s]={since=n,targetGUID=dG}local e={applied=n}table.insert(h,e)f.debuffs[s].historyEntry=e elseif se=="SPELL_AURA_REFRESH"then if d and d.since then f.debuffUptime[s]=(f.debuffUptime[s]or 0)+(n-d.since)end if d and d.historyEntry and not d.historyEntry.removed then d.historyEntry.removed=n end f.debuffs[s]={since=n,targetGUID=dG}local e={applied=n}table.insert(h,e)f.debuffs[s].historyEntry=e elseif se=="SPELL_AURA_REMOVED"then if d and d.since then f.debuffUptime[s]=(f.debuffUptime[s]or 0)+(n-d.since)end if d and d.historyEntry and not d.historyEntry.removed then d.historyEntry.removed=n end f.debuffs[s]=nil end end
function module.Analyze(a,f,c)local m={}local i={}local sc=100 if c.duration<15 then utils.AddIssue(i,"Walka zbyt krotka.")return{score=0,metrics=m,issues=i}end local function AM(l,s,v,p,st,it,pen)table.insert(m,{label=l,spellId=s,valueText=v or"",percent=p,status=st or"info"})utils.AddIssue(i,it)if pen and pen>0 then sc=utils.Clamp(sc-pen,0,100)end end local ffUp=utils.SafePercent(f.debuffUptime[SPELL_FROST_FEVER]or 0,c.duration)local ffSt=utils.StatusForPercent(ffUp,0.90,0.75)AM("Frost Fever uptime",SPELL_FROST_FEVER,utils.FormatPercent(ffUp),ffUp,ffSt,ffSt=="bad"and"Niski uptime Frost Fever."or(ffSt=="warn"and"FF moglby byc wyzszy."or nil),ffSt=="bad"and 10 or(ffSt=="warn"and 5 or 0))local bpUp=utils.SafePercent(f.debuffUptime[SPELL_BLOOD_PLAGUE]or 0,c.duration)local bpSt=utils.StatusForPercent(bpUp,0.90,0.75)AM("Blood Plague uptime",SPELL_BLOOD_PLAGUE,utils.FormatPercent(bpUp),bpUp,bpSt,bpSt=="bad"and"Niski uptime Blood Plague."or(bpSt=="warn"and"BP moglby byc wyzszy."or nil),bpSt=="bad"and 10 or(bpSt=="warn"and 5 or 0))local pof=f.spells[SPELL_PILLAR_OF_FROST]or 0 local expPOF=utils.ExpectedUses(c.duration,COOLDOWN_PILLAR_OF_FROST,10)if expPOF>0 then local pofP=utils.SafePercent(pof,expPOF)local pofSt=utils.StatusForPercent(pofP,0.80,0.60)AM("Pillar of Frost uzycia",SPELL_PILLAR_OF_FROST,string.format("%d/%d",pof,expPOF),math.min(pofP or 0,1),pofSt,pofSt=="bad"and"Za malo Pillar of Frost."or(pofSt=="warn"and"POF uzywany zbyt rzadko."or nil),pofSt=="bad"and 10 or(pofSt=="warn"and 5 or 0))end local kmP=f.procs.killingMachineProcs or 0 local kmC=f.procs.killingMachineConsumed or 0 if kmP>0 then local kmU=utils.SafePercent(kmC,kmP)local kmSt=utils.StatusForPercent(kmU,0.90,0.75)AM("Killing Machine wykorzystanie",SPELL_KILLING_MACHINE,string.format("%s (%d/%d)",utils.FormatPercent(kmU),kmC,kmP),kmU,kmSt,kmSt=="bad"and"Za duzo KM nie zuzyte."or(kmSt=="warn"and"Czesc KM sie marnuje."or nil),kmSt=="bad"and 12 or(kmSt=="warn"and 6 or 0))end local rimeP=f.procs.rimeProcs or 0 local rimeC=f.procs.rimeConsumed or 0 if rimeP>0 then local rimeU=utils.SafePercent(rimeC,rimeP)local rimeSt=utils.StatusForPercent(rimeU,0.85,0.70)AM("Rime wykorzystanie",SPELL_RIME,string.format("%s (%d/%d)",utils.FormatPercent(rimeU),rimeC,rimeP),rimeU,rimeSt,rimeSt=="bad"and"Za duzo Rime nie zuzyte."or(rimeSt=="warn"and"Czesc Rime sie marnuje."or nil),rimeSt=="bad"and 10 or(rimeSt=="warn"and 5 or 0))end if f.rotation and f.rotation.totalCasts>0 then local rS=module.GetRotationScore(f)if rS then local rA=rS.accuracy or 0 local rSt=utils.StatusForPercent(rA,0.85,0.70)AM("Dokladnosc rotacji (APL)",nil,string.format("%.0f%% (%d/%d)",rA*100,rS.optimalCasts,rS.totalCasts),rA,rSt,rSt=="bad"and"Niska dokladnosc."or(rSt=="warn"and"Srednia dokladnosc."or nil),rSt=="bad"and 10 or(rSt=="warn"and 5 or 0))if rS.mistakes and #rS.mistakes>0 then local mC={}for _,mk in ipairs(rS.mistakes)do mC[mk.reason or"Unknown"]=(mC[mk.reason or"Unknown"]or 0)+1 end local sM={}for r,ct in pairs(mC)do table.insert(sM,{reason=r,count=ct})end table.sort(sM,function(x,y)return x.count>y.count end)for j=1,math.min(3,#sM)do local mk=sM[j]if mk.count>=2 then utils.AddIssue(i,string.format("Blad rotacji (%dx): %s",mk.count,mk.reason))end end end end end sc=utils.Clamp(sc,0,100)return{score=sc,metrics=m,issues=i}end
function module.GetLiveScore(a,f)if not f then return nil end local n=GetTime()local d=n-f.startTime if d<5 then return nil end return 100 end
local function CPB(s)if not s then return false end for i=1,40 do local _,_,_,_,_,_,_,_,_,aS=UnitBuff("player",i)if not aS then break end if aS==s then return true end end return false end
local function CTD(s)if not s or not UnitExists("target")then return false,0 end for i=1,40 do local _,_,_,_,_,eT,_,c,_,_,aS=UnitDebuff("target",i)if not aS then break end if aS==s and c=="player"then local r=eT and(eT-GetTime())or 0 return true,r end end return false,0 end
local function TargetLowHP()if not UnitExists("target")then return false end local hp=UnitHealth("target")local maxHP=UnitHealthMax("target")if maxHP==0 then return false end return(hp/maxHP)<0.35 end
local function GRP()return UnitPower("player",6)or 0 end
function module.GetLiveAdvice(a,f)if not f then return""end local n=GetTime()local d=n-f.startTime local rp=GRP()local hasKM=CPB(SPELL_KILLING_MACHINE)local hasRime=CPB(SPELL_RIME)if TargetLowHP()and utils.IsSpellReady(SPELL_SOUL_REAPER)then return"Soul Reaper! (Target <35%)"end if hasKM then return"Obliterate! (Killing Machine)"end if hasRime then return"Howling Blast! (Rime)"end if utils.IsSpellReady(SPELL_PILLAR_OF_FROST)and d>10 then return"Pillar of Frost!"end if UnitExists("target")then local hasBP,bpR=CTD(SPELL_BLOOD_PLAGUE)local hasFF,ffR=CTD(SPELL_FROST_FEVER)if(not hasBP or not hasFF)and d>2 then return"Outbreak! (brak chorob)"end end if rp>=90 then return"Frost Strike! (wysoki RP)"end return""end
function module.GetAdviceSpellIcon(a,f)if not f then return nil end local n=GetTime()local d=n-f.startTime local rp=GRP()local hasKM=CPB(SPELL_KILLING_MACHINE)local hasRime=CPB(SPELL_RIME)if TargetLowHP()and utils.IsSpellReady(SPELL_SOUL_REAPER)then return SPELL_SOUL_REAPER end if hasKM then return SPELL_OBLITERATE end if hasRime then return SPELL_HOWLING_BLAST end if utils.IsSpellReady(SPELL_PILLAR_OF_FROST)and d>10 then return SPELL_PILLAR_OF_FROST end if UnitExists("target")then local hasBP,bpR=CTD(SPELL_BLOOD_PLAGUE)local hasFF,ffR=CTD(SPELL_FROST_FEVER)if(not hasBP or not hasFF)and d>2 then return SPELL_OUTBREAK end end if rp>=90 then return SPELL_FROST_STRIKE end return nil end
local APL_PRIORITY={{id="sr",spellId=SPELL_SOUL_REAPER,name="Soul Reaper",condition=function(s)return s.targetLow and s.srReady end,priority=1,category="execute"},{id="ob_km",spellId=SPELL_OBLITERATE,name="Obliterate(KM)",condition=function(s)return s.killingMachine end,priority=2,category="proc"},{id="hb_rime",spellId=SPELL_HOWLING_BLAST,name="HB(Rime)",condition=function(s)return s.rime end,priority=3,category="proc"},{id="pof",spellId=SPELL_PILLAR_OF_FROST,name="Pillar of Frost",condition=function(s)return s.pofReady and s.duration>10 end,priority=4,category="cooldown"},{id="outbreak",spellId=SPELL_OUTBREAK,name="Outbreak",condition=function(s)return(s.bpMissing or s.ffMissing)and s.duration>2 end,priority=5,category="dot"},{id="ob",spellId=SPELL_OBLITERATE,name="Obliterate",condition=function(s)return true end,priority=6,category="core"},{id="fs",spellId=SPELL_FROST_STRIKE,name="Frost Strike",condition=function(s)return s.rp>=90 end,priority=7,category="dump"}}
local function GCS(a,f)local s={duration=0,targetLow=false,srReady=false,killingMachine=false,rime=false,pofReady=false,bpMissing=false,ffMissing=false,rp=0}if not f then return s end local n=GetTime()s.duration=n-(f.startTime or n)s.targetLow=TargetLowHP()s.srReady=utils.IsSpellReady(SPELL_SOUL_REAPER)s.killingMachine=CPB(SPELL_KILLING_MACHINE)s.rime=CPB(SPELL_RIME)s.pofReady=utils.IsSpellReady(SPELL_PILLAR_OF_FROST)s.rp=GRP()if UnitExists("target")and s.duration>2 then local hasBP,_=CTD(SPELL_BLOOD_PLAGUE)local hasFF,_=CTD(SPELL_FROST_FEVER)s.bpMissing=not hasBP s.ffMissing=not hasFF end return s end
function module.GetNextAPLAction(a,f)local s=GCS(a,f)for _,act in ipairs(APL_PRIORITY)do if act.condition(s)then return act end end return APL_PRIORITY[#APL_PRIORITY]end
function module.GetAPLPriorityList()return APL_PRIORITY end
function module.GetRotationState(a,f)return GCS(a,f)end
function module.EvaluateCast(a,f,sid,ts)local nA=module.GetNextAPLAction(a,f)if not nA then return nil end local isO=(nA.spellId==sid)local pen=0 local rsn=""if not isO then local cP=nil for _,act in ipairs(APL_PRIORITY)do if act.spellId==sid then cP=act.priority break end end if cP then pen=math.abs(nA.priority-cP)*2 rsn=string.format("Powinienes: %s",nA.name)else pen=5 rsn="Spell poza APL"end end return{isOptimal=isO,penalty=pen,reason=rsn,expectedSpell=nA.spellId,expectedName=nA.name,actualSpell=sid}end
function module.RecordCastEvaluation(f,ev,ts)if not f.rotation then return end f.rotation.totalCasts=f.rotation.totalCasts+1 if ev.isOptimal then f.rotation.optimalCasts=f.rotation.optimalCasts+1 else f.rotation.suboptimalCasts=f.rotation.suboptimalCasts+1 f.rotation.penaltySum=f.rotation.penaltySum+ev.penalty table.insert(f.rotation.mistakes,{timestamp=ts,reason=ev.reason,penalty=ev.penalty,expected=ev.expectedName})end end
function module.GetRotationScore(f)if not f.rotation or f.rotation.totalCasts==0 then return nil end return{accuracy=f.rotation.optimalCasts/f.rotation.totalCasts,totalCasts=f.rotation.totalCasts,optimalCasts=f.rotation.optimalCasts,suboptimalCasts=f.rotation.suboptimalCasts,mistakes=f.rotation.mistakes,penaltySum=f.rotation.penaltySum}end
Analyzer:RegisterClassModule(module.class,module)
