local _,Analyzer=...if not Analyzer then return end
local module={}
local utils=Analyzer.utils
module.name="Hunter - Beast Mastery"
module.class="HUNTER"
module.specKey="beastmastery"
module.specIndex=1
module.specId=253
local SPELL_KILL_COMMAND=34026
local SPELL_ARCANE_SHOT=3044
local SPELL_COBRA_SHOT=77767
local SPELL_BESTIAL_WRATH=19574
local SPELL_FOCUS_FIRE=82692
local SPELL_FRENZY=19615
local SPELL_KILL_SHOT=53351
local SPELL_DIRE_BEAST=120679
local SPELL_A_MURDER_OF_CROWS=131894
local SPELL_LYNX_RUSH=120697
local SPELL_GLAIVE_TOSS=117050
local SPELL_BARRAGE=120360
local SPELL_SERPENT_STING=1978
local SPELL_BEAST_CLEAVE=115939
local COOLDOWN_BESTIAL_WRATH=60
local COOLDOWN_DIRE_BEAST=30
local COOLDOWN_CROWS=120
local SOUND_KEY_BESTIAL_WRATH="bestialWrath"
local SOUND_KEY_FRENZY="frenzyStacks"
local SOUND_KEY_KILL_SHOT="killShot"
local TRACKED_BUFFS={[SPELL_BESTIAL_WRATH]=true,[SPELL_FRENZY]=true,[SPELL_FOCUS_FIRE]=true,[SPELL_BEAST_CLEAVE]=true}
local TRACKED_DEBUFFS={[SPELL_SERPENT_STING]=true}
local TRACKED_CASTS={[SPELL_KILL_COMMAND]=true,[SPELL_ARCANE_SHOT]=true,[SPELL_COBRA_SHOT]=true,[SPELL_BESTIAL_WRATH]=true,[SPELL_FOCUS_FIRE]=true,[SPELL_KILL_SHOT]=true,[SPELL_DIRE_BEAST]=true,[SPELL_A_MURDER_OF_CROWS]=true,[SPELL_LYNX_RUSH]=true,[SPELL_GLAIVE_TOSS]=true,[SPELL_BARRAGE]=true,[SPELL_SERPENT_STING]=true}
local function EH(c,s)c[s]=c[s]or{}return c[s]end
function module.SupportsSpec(a)if a.player.class~=module.class then return false end if a.player.specId==module.specId then return true end local sk=utils.NormalizeSpecKey(a.player.specName)if sk==module.specKey then return true end if a.player.specIndex==module.specIndex then return true end return false end
function module.OnPlayerInit(a)if a.player.class~=module.class then return end if a.player.specName~="Unknown"then return end if IsSpellKnown and IsSpellKnown(SPELL_KILL_COMMAND)and IsSpellKnown(SPELL_BESTIAL_WRATH)then a.player.specName="Beast Mastery"a.player.specId=a.player.specId or module.specId a.player.specIndex=a.player.specIndex or module.specIndex end end
function module.GetDefaultSettings()return{sounds={[SOUND_KEY_BESTIAL_WRATH]={enabled=true,sound="Sound\\Interface\\RaidWarning.ogg"},[SOUND_KEY_FRENZY]={enabled=true,sound="Sound\\Interface\\MapPing.ogg"},[SOUND_KEY_KILL_SHOT]={enabled=true,sound="Sound\\Interface\\RaidWarning.ogg"}}}end
function module.GetSoundOptions()return{{key=SOUND_KEY_BESTIAL_WRATH,label="Bestial Wrath Ready"},{key=SOUND_KEY_FRENZY,label="Frenzy 5 Stacks"},{key=SOUND_KEY_KILL_SHOT,label="Kill Shot Available"}}end
function module.IsTrackedBuffSpell(s)return TRACKED_BUFFS[s]==true end
function module.IsTrackedDebuffSpell(s)return TRACKED_DEBUFFS[s]==true end
function module.IsTrackedCast(s)return TRACKED_CASTS[s]==true end
function module.ShouldRecordCast(e)if not e or not e.spellId then return false end return TRACKED_CASTS[e.spellId]==true end
function module.GetProcInfo(s)if s==SPELL_FRENZY then return{soundKey=SOUND_KEY_FRENZY,priority=2}end return nil end
function module.InitFight(_,f)f.counts={killCommandCasts=0,focusFireUsed=0,focusFireAt5=0,killShotCasts=0}f.cooldowns={bestialWrathLast=0,direBeastLast=0,crowsLast=0}f.castLog={}f.buffHistory={}f.debuffHistory={}f.rotation={totalCasts=0,optimalCasts=0,suboptimalCasts=0,mistakes={},penaltySum=0}f.frenzyStacks=0 end
function module.TrackSpellCast(a,s,t)local f=a.fight if not f or not TRACKED_CASTS[s]then return end local n=utils.NormalizeTimestamp(t)f.spells[s]=(f.spells[s]or 0)+1 table.insert(f.castLog,{spellId=s,timestamp=n})if s==SPELL_KILL_COMMAND then f.counts.killCommandCasts=f.counts.killCommandCasts+1 elseif s==SPELL_FOCUS_FIRE then f.counts.focusFireUsed=f.counts.focusFireUsed+1 if(f.frenzyStacks or 0)>=5 then f.counts.focusFireAt5=f.counts.focusFireAt5+1 end f.frenzyStacks=0 elseif s==SPELL_KILL_SHOT then f.counts.killShotCasts=f.counts.killShotCasts+1 a:AddEventLog(n,"Kill Shot",s)elseif s==SPELL_BESTIAL_WRATH then f.cooldowns.bestialWrathLast=n a:AddTimelineEvent(s,n,"cooldown")a:AddEventLog(n,"Bestial Wrath",s)elseif s==SPELL_DIRE_BEAST then f.cooldowns.direBeastLast=n a:AddEventLog(n,"Dire Beast",s)elseif s==SPELL_A_MURDER_OF_CROWS then f.cooldowns.crowsLast=n a:AddTimelineEvent(s,n,"cooldown")a:AddEventLog(n,"A Murder of Crows",s)end local cS={[SPELL_KILL_COMMAND]=true,[SPELL_ARCANE_SHOT]=true,[SPELL_COBRA_SHOT]=true,[SPELL_KILL_SHOT]=true,[SPELL_FOCUS_FIRE]=true}if cS[s]and f.rotation then local ev=module.EvaluateCast(a,f,s,n)if ev then module.RecordCastEvaluation(f,ev,n)end end end
function module.TrackAura(a,se,s,am,t)local f=a.fight if not f then return end local n=utils.NormalizeTimestamp(t)if se=="SPELL_AURA_APPLIED"or se=="SPELL_AURA_APPLIED_DOSE"or se=="SPELL_AURA_REFRESH"then local st=am or 1 local b=f.buffs[s]local isR=(se=="SPELL_AURA_REFRESH")if b and isR and b.since then f.buffUptime[s]=(f.buffUptime[s]or 0)+(n-b.since)end if not b then b={stacks=st,since=n}f.buffs[s]=b else b.stacks=st b.since=b.since or n end if isR then b.since=n end local h=EH(f.buffHistory,s)if isR and b.historyEntry and not b.historyEntry.removed then b.historyEntry.removed=n end if not b.historyEntry or isR then b.historyEntry={applied=n}table.insert(h,b.historyEntry)end if s==SPELL_FRENZY then f.frenzyStacks=st if st>=5 then a:PlayAlertSound(SOUND_KEY_FRENZY,n)end elseif s==SPELL_BESTIAL_WRATH then a:AddTimelineEvent(s,n,"buff")end elseif se=="SPELL_AURA_REMOVED"then local b=f.buffs[s]if b then if b.since then f.buffUptime[s]=(f.buffUptime[s]or 0)+(n-b.since)end if b.historyEntry and not b.historyEntry.removed then b.historyEntry.removed=n end end f.buffs[s]=nil if s==SPELL_FRENZY then f.frenzyStacks=0 end end end
function module.TrackDebuff(a,se,s,dG,dN,t)local f=a.fight if not f or not TRACKED_DEBUFFS[s]then return end local n=utils.NormalizeTimestamp(t)if not f.primaryTargetGUID then f.primaryTargetGUID=dG end if dG and f.primaryTargetGUID and dG~=f.primaryTargetGUID then return end if dG then f.targets[dG]=true end local d=f.debuffs[s]local h=EH(f.debuffHistory,s)if se=="SPELL_AURA_APPLIED"then f.debuffs[s]={since=n,targetGUID=dG}local e={applied=n}table.insert(h,e)f.debuffs[s].historyEntry=e elseif se=="SPELL_AURA_REFRESH"then if d and d.since then f.debuffUptime[s]=(f.debuffUptime[s]or 0)+(n-d.since)end if d and d.historyEntry and not d.historyEntry.removed then d.historyEntry.removed=n end f.debuffs[s]={since=n,targetGUID=dG}local e={applied=n}table.insert(h,e)f.debuffs[s].historyEntry=e elseif se=="SPELL_AURA_REMOVED"then if d and d.since then f.debuffUptime[s]=(f.debuffUptime[s]or 0)+(n-d.since)end if d and d.historyEntry and not d.historyEntry.removed then d.historyEntry.removed=n end f.debuffs[s]=nil end end
function module.Analyze(a,f,c)local m={}local i={}local sc=100 if c.duration<15 then utils.AddIssue(i,"Walka zbyt krotka.")return{score=0,metrics=m,issues=i}end local function AM(l,s,v,p,st,it,pen)table.insert(m,{label=l,spellId=s,valueText=v or"",percent=p,status=st or"info"})utils.AddIssue(i,it)if pen and pen>0 then sc=utils.Clamp(sc-pen,0,100)end end local bwUp=utils.SafePercent(f.buffUptime[SPELL_BESTIAL_WRATH]or 0,c.duration)AM("Bestial Wrath uptime",SPELL_BESTIAL_WRATH,utils.FormatPercent(bwUp),bwUp,bwUp>0.15 and"good"or"warn",bwUp<0.10 and"Niski uptime Bestial Wrath."or nil,bwUp<0.10 and 8 or 0)local bw=f.spells[SPELL_BESTIAL_WRATH]or 0 local expBW=utils.ExpectedUses(c.duration,COOLDOWN_BESTIAL_WRATH,10)if expBW>0 then local bwP=utils.SafePercent(bw,expBW)local bwSt=utils.StatusForPercent(bwP,0.80,0.60)AM("Bestial Wrath uzycia",SPELL_BESTIAL_WRATH,string.format("%d/%d",bw,expBW),math.min(bwP or 0,1),bwSt,bwSt=="bad"and"Za malo Bestial Wrath."or(bwSt=="warn"and"BW uzywany zbyt rzadko."or nil),bwSt=="bad"and 10 or(bwSt=="warn"and 5 or 0))end local ffU=f.counts.focusFireUsed or 0 local ff5=f.counts.focusFireAt5 or 0 if ffU>0 then local ff5P=utils.SafePercent(ff5,ffU)local ffSt=utils.StatusForPercent(ff5P,0.80,0.60)AM("Focus Fire na 5 stackach",SPELL_FOCUS_FIRE,string.format("%s (%d/%d)",utils.FormatPercent(ff5P),ff5,ffU),ff5P,ffSt,ffSt=="bad"and"Focus Fire przy niskich stackach."or(ffSt=="warn"and"Czasem FF bez 5 stackow."or nil),ffSt=="bad"and 10 or(ffSt=="warn"and 5 or 0))end local kc=f.spells[SPELL_KILL_COMMAND]or 0 local avgKCcd=6 local expKC=math.floor(c.duration/avgKCcd)if expKC>0 then local kcP=utils.SafePercent(kc,expKC)local kcSt=utils.StatusForPercent(kcP,0.85,0.70)AM("Kill Command uzycia",SPELL_KILL_COMMAND,string.format("%d/%d",kc,expKC),math.min(kcP or 0,1),kcSt,kcSt=="bad"and"Za malo Kill Command."or(kcSt=="warn"and"KC uzywany zbyt rzadko."or nil),kcSt=="bad"and 8 or(kcSt=="warn"and 4 or 0))end local ssUp=utils.SafePercent(f.debuffUptime[SPELL_SERPENT_STING]or 0,c.duration)local ssSt=utils.StatusForPercent(ssUp,0.90,0.75)AM("Serpent Sting uptime",SPELL_SERPENT_STING,utils.FormatPercent(ssUp),ssUp,ssSt,ssSt=="bad"and"Niski uptime Serpent Sting."or(ssSt=="warn"and"SS moglby byc wyzszy."or nil),ssSt=="bad"and 10 or(ssSt=="warn"and 5 or 0))if f.rotation and f.rotation.totalCasts>0 then local rS=module.GetRotationScore(f)if rS then local rA=rS.accuracy or 0 local rSt=utils.StatusForPercent(rA,0.85,0.70)AM("Dokladnosc rotacji (APL)",nil,string.format("%.0f%% (%d/%d)",rA*100,rS.optimalCasts,rS.totalCasts),rA,rSt,rSt=="bad"and"Niska dokladnosc."or(rSt=="warn"and"Srednia dokladnosc."or nil),rSt=="bad"and 10 or(rSt=="warn"and 5 or 0))if rS.mistakes and #rS.mistakes>0 then local mC={}for _,mk in ipairs(rS.mistakes)do mC[mk.reason or"Unknown"]=(mC[mk.reason or"Unknown"]or 0)+1 end local sM={}for r,ct in pairs(mC)do table.insert(sM,{reason=r,count=ct})end table.sort(sM,function(x,y)return x.count>y.count end)for j=1,math.min(3,#sM)do local mk=sM[j]if mk.count>=2 then utils.AddIssue(i,string.format("Blad rotacji (%dx): %s",mk.count,mk.reason))end end end end end sc=utils.Clamp(sc,0,100)return{score=sc,metrics=m,issues=i}end
function module.GetLiveScore(a,f)if not f then return nil end local n=GetTime()local d=n-f.startTime if d<5 then return nil end return 100 end
local function CPB(s)if not s then return false,0 end for i=1,40 do local _,_,st,_,_,_,_,_,_,aS=UnitBuff("player",i)if not aS then break end if aS==s then return true,st or 1 end end return false,0 end
local function CTD(s)if not s or not UnitExists("target")then return false,0 end for i=1,40 do local _,_,_,_,_,eT,_,c,_,_,aS=UnitDebuff("target",i)if not aS then break end if aS==s and c=="player"then local r=eT and(eT-GetTime())or 0 return true,r end end return false,0 end
local function GFS()local _,fStacks=CPB(SPELL_FRENZY)return fStacks or 0 end
local function TargetLowHP()if not UnitExists("target")then return false end local hp=UnitHealth("target")local maxHP=UnitHealthMax("target")if maxHP==0 then return false end return(hp/maxHP)<0.20 end
function module.GetLiveAdvice(a,f)if not f then return""end local n=GetTime()local d=n-f.startTime if TargetLowHP()and utils.IsSpellReady(SPELL_KILL_SHOT)then return"Kill Shot! Target nisko!"end local fStacks=GFS()if fStacks>=5 then return"Focus Fire! (5 stackow)"end if utils.IsSpellReady(SPELL_KILL_COMMAND)then return"Kill Command!"end if utils.IsSpellReady(SPELL_BESTIAL_WRATH)and d>10 then return"Bestial Wrath gotowe!"end if UnitExists("target")then local hasSS,ssR=CTD(SPELL_SERPENT_STING)if not hasSS and d>2 then return"Naloz Serpent Sting!"end if hasSS and ssR>0 and ssR<4 then return"Serpent Sting wygasa!"end end return""end
function module.GetAdviceSpellIcon(a,f)if not f then return nil end local n=GetTime()local d=n-f.startTime if TargetLowHP()and utils.IsSpellReady(SPELL_KILL_SHOT)then return SPELL_KILL_SHOT end local fStacks=GFS()if fStacks>=5 then return SPELL_FOCUS_FIRE end if utils.IsSpellReady(SPELL_KILL_COMMAND)then return SPELL_KILL_COMMAND end if utils.IsSpellReady(SPELL_BESTIAL_WRATH)and d>10 then return SPELL_BESTIAL_WRATH end if UnitExists("target")then local hasSS,ssR=CTD(SPELL_SERPENT_STING)if not hasSS and d>2 then return SPELL_SERPENT_STING end if hasSS and ssR>0 and ssR<4 then return SPELL_SERPENT_STING end end return nil end
local APL_PRIORITY={{id="ks",spellId=SPELL_KILL_SHOT,name="Kill Shot",condition=function(s)return s.targetLow and s.ksReady end,priority=1,category="execute"},{id="ff5",spellId=SPELL_FOCUS_FIRE,name="Focus Fire(5)",condition=function(s)return s.frenzyStacks>=5 end,priority=2,category="buff"},{id="bw",spellId=SPELL_BESTIAL_WRATH,name="Bestial Wrath",condition=function(s)return s.bwReady and s.duration>10 end,priority=3,category="cooldown"},{id="kc",spellId=SPELL_KILL_COMMAND,name="Kill Command",condition=function(s)return s.kcReady end,priority=4,category="core"},{id="ss_m",spellId=SPELL_SERPENT_STING,name="Serpent Sting",condition=function(s)return s.ssMissing and s.duration>2 end,priority=5,category="dot"},{id="ss_r",spellId=SPELL_SERPENT_STING,name="SS(refresh)",condition=function(s)return s.ssExpiring end,priority=6,category="dot"},{id="as",spellId=SPELL_ARCANE_SHOT,name="Arcane Shot",condition=function(s)return s.focus>=60 end,priority=7,category="dump"},{id="cs",spellId=SPELL_COBRA_SHOT,name="Cobra Shot",condition=function(s)return true end,priority=8,category="builder"}}
local function GCS(a,f)local s={duration=0,targetLow=false,ksReady=false,frenzyStacks=0,bwReady=false,kcReady=false,ssMissing=false,ssExpiring=false,focus=0}if not f then return s end local n=GetTime()s.duration=n-(f.startTime or n)s.targetLow=TargetLowHP()s.ksReady=utils.IsSpellReady(SPELL_KILL_SHOT)s.frenzyStacks=GFS()s.bwReady=utils.IsSpellReady(SPELL_BESTIAL_WRATH)s.kcReady=utils.IsSpellReady(SPELL_KILL_COMMAND)s.focus=UnitPower("player",2)or 0 if UnitExists("target")and s.duration>2 then local hasSS,ssR=CTD(SPELL_SERPENT_STING)s.ssMissing=not hasSS s.ssExpiring=hasSS and ssR>0 and ssR<4 end return s end
function module.GetNextAPLAction(a,f)local s=GCS(a,f)for _,act in ipairs(APL_PRIORITY)do if act.condition(s)then return act end end return APL_PRIORITY[#APL_PRIORITY]end
function module.GetAPLPriorityList()return APL_PRIORITY end
function module.GetRotationState(a,f)return GCS(a,f)end
function module.EvaluateCast(a,f,sid,ts)local nA=module.GetNextAPLAction(a,f)if not nA then return nil end local isO=(nA.spellId==sid)local pen=0 local rsn=""if not isO then local cP=nil for _,act in ipairs(APL_PRIORITY)do if act.spellId==sid then cP=act.priority break end end if cP then pen=math.abs(nA.priority-cP)*2 rsn=string.format("Powinienes: %s",nA.name)else pen=5 rsn="Spell poza APL"end end return{isOptimal=isO,penalty=pen,reason=rsn,expectedSpell=nA.spellId,expectedName=nA.name,actualSpell=sid}end
function module.RecordCastEvaluation(f,ev,ts)if not f.rotation then return end f.rotation.totalCasts=f.rotation.totalCasts+1 if ev.isOptimal then f.rotation.optimalCasts=f.rotation.optimalCasts+1 else f.rotation.suboptimalCasts=f.rotation.suboptimalCasts+1 f.rotation.penaltySum=f.rotation.penaltySum+ev.penalty table.insert(f.rotation.mistakes,{timestamp=ts,reason=ev.reason,penalty=ev.penalty,expected=ev.expectedName})end end
function module.GetRotationScore(f)if not f.rotation or f.rotation.totalCasts==0 then return nil end return{accuracy=f.rotation.optimalCasts/f.rotation.totalCasts,totalCasts=f.rotation.totalCasts,optimalCasts=f.rotation.optimalCasts,suboptimalCasts=f.rotation.suboptimalCasts,mistakes=f.rotation.mistakes,penaltySum=f.rotation.penaltySum}end
Analyzer:RegisterClassModule(module.class,module)
